const fs = require('fs');
const path = require('path');

// Create icons directory
const iconsDir = path.join(__dirname, '../src/icons');
if (!fs.existsSync(iconsDir)) {
  fs.mkdirSync(iconsDir, { recursive: true });
}

// Simple green square PNG files in base64 format
const iconData = {
  16: 'iVBORw0KGgoAAAANSUhEUgAAABAAAAAQCAYAAAAf8/9hAAAAVElEQVQ4T2NkoDJgpLI+NEPBBvz//x8LcnNzg8RZIBHM6gsWLDAxMTFhYWEhKgAyAMgGsgEk+x+qAWQDSPY/VANIBpDsf6gGkAwg2f9QDSAZAABxOA8R5oP9XQAAAABJRU5ErkJggg==',
  32: 'iVBORw0KGgoAAAANSUhEUgAAACAAAAAgCAYAAABzenr0AAAAXElEQVRYR+2WsQ0AMAhDzf7/6EcJBZIg3VXwSyEBAXhJAQ7iCQBGpQsAjEIbABiFPgAwCl0AYBTaAMAo9AGAUegCAKPQBgBGoQ8AjEIXABiFNgAwCn0AYBS6AMAotAFAN8YtKiFRhKWcAAAAAElFTkSuQmCC',
  48: 'iVBORw0KGgoAAAANSUhEUgAAADAAAAAwCAYAAABXAvmHAAAAYElEQVRoQ+2YsQ0AIAwDzf5/6EcJBcIgHVbwSyEBAXhJAQ7iCQBGpQsAjEIbABiFPgAwCl0AYBTaAMAo9AGAUegCAKPQBgBGoQ8AjEIXABiFNgAwCn0AYBS6AMAotAFAN7BPKiFRhJfNAAAAAElFTkSuQmCC',
  128: '',
};

// Create each icon size
Object.entries(iconData).forEach(([size, data]) => {
  const buffer = Buffer.from(data, 'base64');
  const filepath = path.join(iconsDir, `icon${size}.png`);
  fs.writeFileSync(filepath, buffer);
  console.log(`Created icon${size}.png (${buffer.length} bytes)`);
});

